import {
	useNavigate,
	Form,
	useNavigation,
	useActionData,
} from "react-router-dom";

import classes from "./EventForm.module.css";

function EventForm({ method, event }) {
	const navigate = useNavigate();
	const navigation = useNavigation();
	const data = useActionData();

	const isSubmitting = navigation.state === "submitting";

	// console.log(data);

	function cancelHandler() {
		navigate("..");
	}

	return (
		<Form method={method} className={classes.form}>
			{/* {data && data.errors && (
				<ul>
					{Object.values(data.errors).map((err) => (
						<li key={err}>{err}</li>
					))}
				</ul>
			)} */}
			{/* {console.log(data)} */}
			<p>
				<label htmlFor="title">Title</label>
				{data && data.errors && <li>{data.errors.title}</li>}
				<input
					id="title"
					type="text"
					name="title"
					required
					defaultValue={event ? event.title : ""}
				/>
			</p>
			<p>
				<label htmlFor="image">Image</label>
				{data && data.errors && <li>{data.errors.image}</li>}
				<input
					id="image"
					type="url"
					name="image"
					required
					defaultValue={event ? event.image : ""}
				/>
			</p>
			<p>
				<label htmlFor="date">Date</label>
				{data && data.errors && <li>{data.errors.date}</li>}
				<input
					id="date"
					type="date"
					name="date"
					required
					defaultValue={event ? event.date : ""}
				/>
			</p>
			<p>
				<label htmlFor="description">Description</label>
				{data && data.errors && <li>{data.errors.description}</li>}
				<textarea
					id="description"
					name="description"
					rows="5"
					required
					defaultValue={event ? event.description : ""}
				/>
			</p>
			<div className={classes.actions}>
				<button
					type="button"
					onClick={cancelHandler}
					disabled={isSubmitting}
				>
					Cancel
				</button>
				<button disabled={isSubmitting}>
					{isSubmitting ? "Submitting..." : "Save"}
				</button>
			</div>
		</Form>
	);
}

export default EventForm;

export const action = async ({ request, params }) => {
	const data = await request.formData();

	const eventDataObj = {
		title: data.get("title"),
		image: data.get("image"),
		date: data.get("date"),
		description: data.get("description"),
	};

	let url = "http://localhost:8080/events";

	if (request.method === "patch") {
		url = "http://localhost:8080/events/" + params.eventId;
	}

	const response = await fetch(url, {
		method: request.method,
		headers: {
			"Content-Type": "application/json",
		},
		body: JSON.stringify(eventDataObj),
	});

	if (response.status === 422) {
		return response;
	}

	if (!response.ok) {
		throw json({ message: "Couldn't save the event :(" }, { status: 500 });
	}

	return redirect("/events");
};
