import "./ExpenseDate.css";

function ExpenseDate(props) {
    // const month = props.date.toLocaleString("en-US", { month: "long" });
    // const year = props.date.getFullyear();
    // const day = props.date.toLocaleString("en-US", { day: "2-digit" });
    const month = props.expense.date.toLocaleString("en-US", { month: "long" });
    const year = props.expense.date.getFullyear();
    const day = props.expense.date.toLocaleString("en-US", { day: "2-digit" });

    console.log(props);
    console.log(props.expense.date);
    console.log(props.date);
    // const month = props.toLocaleString("en-US", { month: "long" });
    // const year = props.getFullyear();
    // const day = props.toLocaleString("en-US", { day: "2-digit" });

    return (
        <div className="expense-date">
            <div className="expense-date__month">{month}</div>
            <div className="expense-date__year">{year}</div>
            <div className="expense-date__day">{day}</div>
        </div>
    );
}

export default ExpenseDate;
