import Input from "../UI/Input";
import styles from "./MealsItemForm.module.css";

const MealsItemForm = (props) => {
    return (
        <form className={styles.form}>
            <Input
                label="Amount"
                input={{
                    id: "amount_" + props.id,
                    type: "number",
                    min: "1",
                    max: "10",
                    step: "1",
                    defaultValue: "1",
                }}
            />
            <button>+ Add</button>
        </form>
    );
};

export default MealsItemForm;
