import { useState } from "react";
import useInput from "../hooks/use-input";

const SimpleInput = (props) => {
    // FOR NAME INPUT:
    const {
        value: enteredName,
        isValid: isEnteredNameValid,
        hasError: nameHasError,
        InputChangeHandler: nameInputChangeHandler,
        InputBlurHandler: nameInputBlurHandler,
        reset: resetName,
    } = useInput((value) => value.trim().length !== 0);

    // FOR EMAIL INPUT:
    const {} = useInput(
        (value) => value.trim().length !== 0 && value.trim().includes("@")
    );

    // STATES FOR EMAIL:
    const [enteredEmail, setEnteredEmail] = useState("");
    const [emailWasTouched, setEmailWasTouched] = useState(false);

    const isEnteredEmailValid =
        enteredEmail.trim().length !== 0 && enteredEmail.trim().includes("@");
    const emailInputIsInvalid = !isEnteredEmailValid && emailWasTouched;

    let isFormValid = false;

    if (isEnteredNameValid && isEnteredEmailValid) {
        isFormValid = true;
    }

    // EMAIL:
    const emailInputChangeHandler = (e) => {
        setEnteredEmail(e.target.value);
    };

    const emailInputBlurHandler = (e) => {
        setEmailWasTouched(true);
    };

    // OVERALL FORM:
    const submissionHandler = (e) => {
        e.preventDefault();

        // setNameWasTouched(true);

        if (!isEnteredNameValid) {
            console.log("returned");
            return;
        }

        console.log(enteredName);
        console.log(enteredEmail);

        resetName();

        setEnteredEmail("");
        setEmailWasTouched(false);
    };

    const nameInputClasses = nameHasError
        ? "form-control invalid"
        : "form-control";

    const emailInputClasses = emailInputIsInvalid
        ? "form-control invalid"
        : "form-control";

    return (
        <form onSubmit={submissionHandler}>
            <div className={nameInputClasses}>
                <label htmlFor="name">Your Name</label>
                <input
                    type="text"
                    id="name"
                    onChange={nameInputChangeHandler}
                    onBlur={nameInputBlurHandler}
                    value={enteredName}
                />
                {nameHasError && (
                    <p className="error-text">Enter a valid name</p>
                )}
            </div>
            <div className={emailInputClasses}>
                <label htmlFor="email">Your E-mail</label>
                <input
                    type="email"
                    id="email"
                    onChange={emailInputChangeHandler}
                    onBlur={emailInputBlurHandler}
                    value={enteredEmail}
                />
                {emailInputIsInvalid && (
                    <p className="error-text">Enter a valid E-mail</p>
                )}
            </div>
            <div className="form-actions">
                <button disabled={!isFormValid}>Submit</button>
            </div>
        </form>
    );
};

export default SimpleInput;
