import { useState } from "react";

const SimpleInput = (props) => {
    const [enteredName, setEnteredName] = useState("");
    // const [isEnteredNameValid, setIsEnteredNameValid] = useState(false);
    const [nameWasTouched, setNameWasTouched] = useState(false);

    const isEnteredNameValid = enteredName.trim().length !== 0;
    const nameInputIsInvalid = !isEnteredNameValid && nameWasTouched;

    const nameInputChangeHandler = (e) => {
        setEnteredName(e.target.value);
        // setNameWasTouched(false);
    };

    const nameInputBlurHandler = (e) => {
        setNameWasTouched(true);

        // if (enteredName.trim().length === 0) {
        //     console.log("lost focus");
        //     setIsEnteredNameValid(false);
        // }
    };

    const submissionHandler = (e) => {
        e.preventDefault();

        setNameWasTouched(true);

        if (!isEnteredNameValid) {
            console.log("returned");
            // setIsEnteredNameValid(false);
            return;
        }

        // setIsEnteredNameValid(true);
        console.log(enteredName);

        setEnteredName("");
    };

    const inputClasses = nameInputIsInvalid
        ? "form-control invalid"
        : "form-control";

    return (
        <form onSubmit={submissionHandler}>
            <div className={inputClasses}>
                <label htmlFor="name">Your Name</label>
                <input
                    type="text"
                    id="name"
                    onChange={nameInputChangeHandler}
                    onBlur={nameInputBlurHandler}
                    value={enteredName}
                />
                {nameInputIsInvalid && (
                    <p className="error-text">Enter a valid input</p>
                )}
            </div>
            <div className="form-actions">
                <button>Submit</button>
            </div>
        </form>
    );
};

export default SimpleInput;
