import { createSlice } from "@reduxjs/toolkit";
import { act } from "react-dom/test-utils";
import { uiActions } from "./ui-slice";

const cartSlice = createSlice({
	name: "cart",
	initialState: {
		itemsArr: [],
		totalQuantity: 0,
	},
	reducers: {
		addToCart(state, action) {
			const newItem = action.payload;

			const existingItem = state.itemsArr.find(
				(item) => item.id === newItem.id
			);

			state.totalQuantity++;

			if (!existingItem) {
				state.itemsArr.push({
					id: newItem.id,
					price: newItem.price,
					totalPrice: newItem.price,
					quantity: 1,
					name: newItem.title,
				});
			} else {
				existingItem.quantity++;
				existingItem.totalPrice =
					existingItem.totalPrice + newItem.price;
			}
		},
		removeFromCart(state, action) {
			const id = action.payload;
			const existingItem = state.itemsArr.find((item) => item.id === id);
			state.totalQuantity--;
			if (existingItem.quantity === 1) {
				state.itemsArr = state.itemsArr.filter(
					(item) => item.id !== id
				);
			} else {
				existingItem.quantity--;
				existingItem.totalPrice =
					existingItem.totalPrice - existingItem.price;
			}
		},
	},
});

export const sendCartData = async (cart) => {
	return async (dispatcher) => {
		dispatcher(
			uiActions.showNotification({
				status: "pending",
				title: "Sending...",
				message: "Sending cart data...!",
			})
		);

		const sendRequest = async () => {
			const response = await fetch(
				"https://redux-cart-8b606-default-rtdb.firebaseio.com/cart.json",
				{
					method: "PUT",
					body: JSON.stringify(cart),
				}
			);

			if (!response.ok) {
				throw new Error("Failed to fetch");
			}
		};
		try {
			await sendRequest();

			dispatcher(
				uiActions.showNotification({
					status: "success",
					title: "Success!!!",
					message: "Successfully sent the cart data :)",
				})
			);
		} catch (error) {
			dispatcher(
				uiActions.showNotification({
					status: "error",
					title: "Error",
					message: "Sending cart data failed :(",
				})
			);
		}
	};
};

export const cartActions = cartSlice.actions;

export default cartSlice;
