import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { uiActions } from "./store/ui-slice";

import Cart from "./components/Cart/Cart";
import Layout from "./components/Layout/Layout";
import Products from "./components/Shop/Products";
import Notification from "./components/UI/Notification";

function App() {
	const dispatcher = useDispatch();
	const showCart = useSelector((state) => state.ui.isCartShown);
	const cart = useSelector((state) => state.cart);
	const notification = useSelector((state) => state.ui.showNotification);

	console.log(notification);

	let isInitial = true;

	useEffect(() => {
		const sendCartData = async () => {
			// dispatcher(
			// 	uiActions.showNotification({
			// 		status: "pending",
			// 		title: "Sending...",
			// 		message: "Sending cart data...!",
			// 	})
			// );

			const response = await fetch(
				"https://redux-cart-8b606-default-rtdb.firebaseio.com/cart.json",
				{
					method: "PUT",
					body: JSON.stringify(cart),
				}
			);

			if (!response.ok) {
				throw new Error("Failed to fetch");
			}

			// dispatcher(
			// 	uiActions.showNotification({
			// 		status: "success",
			// 		title: "Success!!!",
			// 		message: "Successfully sent the cart data :)",
			// 	})
			// );
		};

		if (isInitial) {
			isInitial = false;
			return;
		}

		sendCartData().catch((error) => {
			// dispatcher(
			// 	uiActions.showNotification({
			// 		status: "error",
			// 		title: "Error",
			// 		message: "Sending cart data failed :(",
			// 	})
			// );
		});
	}, [cart, dispatcher]);

	return (
		<>
			{notification && (
				<Notification
					status={notification.status}
					title={notification.title}
					message={notification.message}
				/>
			)}
			<Layout>
				{showCart && <Cart />}
				<Products />
			</Layout>
		</>
	);
}

export default App;
