import { useSelector, useDispatch } from "react-redux";
import classes from "./Counter.module.css";
import { useState } from "react";

const Counter = () => {
    // const [toggleCounter, setToggleCounter] = useState(false);
    const counter = useSelector((state) => state.counter);
    const show = useSelector((state) => state.showCounter);
    const dispatcher = useDispatch();

    const incrementHandler = () => {
        dispatcher({ type: "increment" });
    };

    const decrementHandler = () => {
        dispatcher({ type: "decrement" });
    };

    const increaseHandler = () => {
        dispatcher({ type: "increase", amount: 10 });
    };

    // const decreaseHandler = () => {
    //     dispatcher({ type: "decrease", amount: 10 });
    // };

    const toggleCounterHandler = () => {
        // setToggleCounter((prev) => !prev);
        dispatcher({ type: "toggle" });
    };

    return (
        <main className={classes.counter}>
            <h1>Redux Counter</h1>
            {!show && <div className={classes.value}>{counter}</div>}
            {show && <div className={classes.value}>&uarr; Counter &darr;</div>}
            <div>
                <button onClick={incrementHandler}>&uarr; Increment</button>
                <button onClick={increaseHandler}>&uarr; Increase by 10</button>
                <button onClick={decrementHandler}>&darr; Decrement</button>
                {/* <button onClick={decreaseHandler}>&darr; Decrease by 10</button> */}
            </div>
            <button onClick={toggleCounterHandler}>Toggle Counter</button>
        </main>
    );
};

export default Counter;
