import React, { useEffect, useState } from "react";

import Tasks from "./components/Tasks/Tasks";
import NewTask from "./components/NewTask/NewTask";
import useHttp from "./hooks/use-http";

function App() {
    const [tasks, setTasks] = useState([]);

    const transformTasks = (transformObj) => {
        const loadedTasks = [];

        for (const taskKey in transformObj) {
            loadedTasks.push({ id: taskKey, text: transformObj[taskKey].text });
        }

        setTasks(loadedTasks);
    };

    const {
        isLoading,
        error,
        sendRequest: fetchTasks,
    } = useHttp(
        {
            url: "https://task-app-b00fc-default-rtdb.firebaseio.com/tasks.json",
        },
        transformTasks
    );

    // const fetchTasks = async (taskText) => {
    //     setIsLoading(true);
    //     setError(null);
    //     try {
    //         const response = await fetch(
    //             "https://task-app-b00fc-default-rtdb.firebaseio.com/tasks.json"
    //         );

    //         if (!response.ok) {
    //             throw new Error("Request failed!");
    //         }

    //         const data = await response.json();
    //     } catch (err) {
    //         setError(err.message || "Something went wrong!");
    //     }
    //     setIsLoading(false);
    // };

    useEffect(() => {
        fetchTasks();
    }, []);

    const taskAddHandler = (task) => {
        setTasks((prevTasks) => prevTasks.concat(task));
    };

    return (
        <React.Fragment>
            <NewTask onAddTask={taskAddHandler} />
            <Tasks
                items={tasks}
                loading={isLoading}
                error={error}
                onFetch={fetchTasks}
            />
        </React.Fragment>
    );
}

export default App;
